import React, { Component } from "react";
import TransaksiService from "../services/TransaksiService";

class ListTransaksiComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      transaksi: [],
      searchText: "",
      filteredTransaksis: [],
    };
    this.addTransaksi = this.addTransaksi.bind(this);
    this.editTransaksi = this.editTransaksi.bind(this);
    this.deleteTransaksi = this.deleteTransaksi.bind(this);
    this.onChangeSearchText = this.onChangeSearchText.bind(this);
  }

  deleteTransaksi(id) {
    TransaksiService.deleteTransaksi(id).then((res) => {
      this.setState((prevState) => ({
        transaksi: prevState.transaksi.filter(
          (transaksi) => transaksi.id !== id
        ),
        filteredTransaksi: prevState.filteredTransaksi.filter(
          (transaksi) => transaksi.id !== id
        ),
      }));
    });
  }
  viewTransaksi(id) {
    this.props.history.push(`/view-transaksi/${id}`);
  }
  editTransaksi(id) {
    this.props.history.push(`/add-transaksi/${id}`);
  }
  onChangeSearchText(event) {
    const searchText = event.target.value;
    this.setState({ searchText }, () => {
      // Update the filteredUsers state in real-time
      const filteredTransaksi = this.state.transaksi.filter((transaksi) => {
        return (
          transaksi.date.toLowerCase().includes(searchText.toLowerCase()) ||
          transaksi.description
            .toLowerCase()
            .includes(searchText.toLowerCase()) ||
          transaksi.amount.toLowerCase().includes(searchText.toLowerCase()) ||
          transaksi.status.toLowerCase().includes(searchText.toLowerCase()) ||
          transaksi.receiver.toLowerCase().includes(searchText.toLowerCase()) ||
          transaksi.jk.toLowerCase().includes(searchText.toLowerCase()) ||
          transaksi.no_telp.toLowerCase().includes(searchText.toLowerCase()) ||
          transaksi.address.toLowerCase().includes(searchText.toLowerCase())
        );
      });
      this.setState({ filteredTransaksis });
    });
  }

  componentDidMount() {
    TransaksiService.getTransaksis().then((res) => {
      if (res.data == null) {
        this.props.history.push("/add-transaksi/_add");
      }
      this.setState({ transaksi: res.data, filteredTransaksis: res.data });
    });
  }

  addTransaksi() {
    this.props.history.push("/add-transaksi/_add");
  }

  render() {
    return (
      <div>
        <h2 className="text-center">List Transaksi</h2>
        <br></br>
        <div style={{ fontSize: "13px" }}>
          <div className="row">
            <div className="col-md-4">
              <div className="form-group">
                <label htmlFor="searchText">Search:</label>
                <input
                  type="text"
                  placeholder="Apa Yang Mau Kamu Cari ?"
                  className="form-control"
                  id="searchText"
                  value={this.state.searchText}
                  onChange={this.onChangeSearchText}
                />
              </div>
            </div>
          </div>

          <br></br>

          <div className="row">
            <table className="table table-striped table-bordered">
              <thead>
                <tr>
                  <th style={{ width: "1%", textAlign: "center" }}>ID</th>
                  <th style={{ width: "30%", textAlign: "center" }}>Tanggal</th>
                  <th style={{ width: "30%", textAlign: "center" }}>
                    Deskripsi
                  </th>
                  <th style={{ width: "1%", textAlign: "center" }}>
                    Nominal Transaksi
                  </th>
                  <th style={{ width: "1%", textAlign: "center" }}>Status</th>
                  <th style={{ width: "1%", textAlign: "center" }}>
                    Penangung Jawab
                  </th>
                  <th style={{ width: "1%", textAlign: "center" }}>
                    Jenis Kelamin
                  </th>
                  <th style={{ width: "1%", textAlign: "center" }}>
                    No Telpon
                  </th>
                  <th style={{ width: "30%", textAlign: "center" }}>Alamat</th>
                  <th style={{ textAlign: "center" }}>Action</th>
                </tr>
              </thead>
              <tbody>
                {this.state.filteredTransaksis.map((transaksi) => (
                  <tr key={transaksi.id}>
                    <td>{transaksi.id}</td>
                    <td>{transaksi.date}</td>
                    <td>{transaksi.description}</td>
                    <td>{transaksi.amount}</td>
                    <td>{transaksi.status}</td>
                    <td>{transaksi.receiver}</td>
                    <td>{transaksi.jk}</td>
                    <td>{transaksi.no_telp}</td>
                    <td>{transaksi.address}</td>
                    <td>
                      <div className="d-flex justify-content-between">
                        <button
                          onClick={() => this.editTransaksi(transaksi.id)}
                          className="btn btn-info"
                        >
                          Update
                        </button>
                        <button
                          style={{ marginLeft: "10px" }}
                          onClick={() => this.deleteTransaksi(transaksi.id)}
                          className="btn btn-danger"
                        >
                          Delete
                        </button>
                        <button
                          style={{ marginLeft: "10px" }}
                          onClick={() => this.viewTransaksi(transaksi.id)}
                          className="btn btn-info"
                        >
                          View
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
          <div className="row">
            <div className="col-md-7 d-flex justify-content-end">
              <button className="btn btn-primary" onClick={this.addTransaksi}>
                Add Transaksi
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default ListTransaksiComponent;
